char cur;
int main()
{
	ios::sync_with_stdio(false);
    cout.tie(0);
    cin>>cur;//一样先读
	int curleft=0,curright=0;//现在读过了几个左几个右
	while(true&&(cur!='@'))//@就退出
    {
		if(cur=='(') curleft++;//左个数++
		if(cur==')') curright++;//右个数++
		if(curright>curleft){//如果某一时刻右>左，就错误了
			cout<<"NO"<<endl;return 0;
		}
		cin>>cur;
    }
	if(curleft>curright)cout<<"NO"<<endl;//如果结束后左的比右的多，就错误了
	else cout<<"YES"<<endl;//其他都对
	return 0;
}
