int main()
{
    char kuohao[1000000];
    cin>>kuohao;
    stack<char> bra;
    int l=strlen(kuohao);
    for(int i=0;i<l;i++)
    {
        if(kuohao[i]=='(')//左括号入栈
        {
            bra.push(kuohao[i]);
        }
        else if(kuohao[i]==')'&&bra.empty()==0)//一定要判空，否则会RE
        {
            bra.pop();
        }
        else if(bra.empty()&&kuohao[i]==')')//这里也是，必须要判空。
        {
            cout<<"NO";
            return 0;
