#include<bits/stdc++.h>
using namespace std;
const int N=110,M=1010;
int h[N*2],e[M*2],ne[M*2],n,m,x[N*2],y[N*2],s,end,idx;
double w[M*2],dist[N*2];
bool st[N*2];
double distance(int x1,int y1,int x2,int y2)
{
	return sqrt(abs(x1-x2)*abs(x1-x2)+abs(y1-y2)*abs(y1-y2));
}
void add(int a,int b,double c)
{
	e[idx]=b,w[idx]=c,ne[idx]=h[a],h[a]=idx++;
}
void spfa()
{
	for(int i=1;i<=n;i++) dist[i]=1000000000.0;
	dist[s]=0;
	queue<int> q;
	q.push(s);
	st[s]=true;
	while(!q.empty())
	{
		int t=q.front();
		q.pop();
		st[t]=false;
		for(int i=h[t];i!=-1;i=ne[i])
		{
			int j=e[i];
			if(dist[j]>dist[t]+w[i])
			{
				dist[j]=dist[t]+w[i];
				if(!st[j])
				{
					st[j]=true;
					q.push(j);
				}
			}
		}
	}
	cout<<fixed<<setprecision(2)<<dist[end];
}
int main()
{
    cin>>n;
    memset(h,-1,sizeof h);
    for(int i=1;i<=n;i++) cin>>x[i]>>y[i];
    cin>>m;
    while(m--)
    {
    	int a,b;
    	cin>>a>>b;
    	add(a,b,distance(x[a],y[a],x[b],y[b])),add(b,a,distance(x[a],y[a],x[b],y[b]));
	}
	cin>>s>>end;
	spfa();
    return 0;
}
```
