 for k:=1 to n do
  for i:=1 to n do
   for j:=1 to n do
    if (i<>j)and(j<>k)and(k<>i)and(f[i,j]>f[i,k]+f[k,j])
    then f[i,j]:=f[i,k]+f[k,j];
