```cpp
#include<bits/stdc++.h>

using namespace std;

queue<int> qx;
queue<int> qy;
queue<int> qs;//定义x,y,和step的队列 
char Map[1001][1001];//储存地图 
bool usd[1001][1001];//储存标记 
int n;//地图大小 
int sx, sy, ex, ey;//起点和终点 
int movex[] = {0, 0, 1, 0, -1};
int movey[] = {0, 1, 0, -1, 0};//这样写方便修改代码也短 

int bfs() {
	qx.push(sx);
	qy.push(sy);
	qs.push(0); 
	while(! qx.empty()) {
		int vs = qs.front();
		int vy = qy.front();
		int vx = qx.front();
		qs.pop();
		qx.pop();
		qy.pop();

		//cout << vx << " " << vy << endl;

		if(vx == ex && vy == ey) return vs;//到达终点时直接返回 

		for(register unsigned int i = 1; i <= 4; ++i) {
			int mx = vx + movex[i];
			int my = vy + movey[i];
			if(usd[my][mx] || Map[my][mx] == '1' || mx < 1 || my < 1 || mx > n || my > n) {
				continue;
			}
			usd[my][mx] = true;
			qx.push(mx);
			qy.push(my);
			qs.push(vs + 1);
		}//这样就不用写一堆if了 
	}
}

int main(void) {
	cin >> n;
	for(register unsigned int i = 1; i <= n; ++i) {
		for(register unsigned int j = 1; j <= n; ++j) {
			cin >> Map[i][j];//字符输入可以不用空格 
		}
	}
	cin >> sy >> sx >> ey >> ex;
	cout << bfs();//输出答案
	while(1) cout << endl;
	return 0;
}
```

