上代码（inline等是常数优化，另外[关于namespace的技巧](https://banana.blog.luogu.org/tong-guo-namespace-fang-zhi-ming-ming-chong-tu)）

```cpp
#include <cstdio>
#include <cstring>

using namespace std;

namespace BANANA {

#define N 1005

struct Node {
	int x, y;
};

struct queue {// 手工封装queue 
	Node a[N*N];
	int head, tail;
	queue() {// queue初始化 
		head = 0, tail = -1;
	}
	inline void push(int x, int y) {
		a[++tail] = (Node){x, y};
	}
	inline void pop() {
		head++;
	}
	inline bool empty() {
		return tail < head;
	}
	inline Node front() {
		return a[head];
	}
};

int dx[] = {0, 1, 0, -1}, dy[] = {1, 0, -1, 0};// 偏移量，懒得分类讨论 
int n, sx, sy, ex, ey;// sx,sy起点，ex,ey终点 
char map[N][N];// 输入地图 
int ans[N][N];// ans[i][j]记录到i,j的步数 
queue q;

inline void bfs() {
	memset(ans, -1, sizeof ans);// 先全设为-1表示都未访问过 
	ans[sx][sy] = 0;
	q.push(sx, sy);
	while (!q.empty()) {
		Node u = q.front();
		q.pop();
		for (int i = 0; i < 4; i++) {// 利用dx[],dy[]减少码量 
			int x = u.x + dx[i], y = u.y + dy[i];
			if (x > 0 && x <= n && y > 0 && y <= n && map[x][y] == '0' && ans[x][y] == -1) 
				ans[x][y] = ans[u.x][u.y] + 1, q.push(x, y);// 当ans[i][j]!=-1时，相当于标记为已访问过 
			if (x == ex && y == ey)//找到车站就直接返回 
				return;
		}
	}
}

inline void main() {
	scanf("%d", &n);
	for (int i = 1; i <= n; i++)
		scanf("%s", map[i]+1);
	scanf("%d%d%d%d", &sx, &sy, &ex, &ey);
	bfs();
	printf("%d\n", ans[ex][ey]);
}
}

int main() {
	BANANA::main();
	return 0;
}
```

