void clear()
{
    memset(vis,0,sizeof(vis));
    memset(q,0,sizeof(q));
}
//两次bfs之间清空一次队列和标记
int bfs(int a,int b)
{
    vis[a][b]=1;
    q[1].x=a;
    q[1].y=b;
    q[1].t=0;
    int head=1,tail=1;
    while(head<=tail)
    {
        data exp=q[head++];
        for(int i=0;i<12;i++)
        {
            int nx=exp.x+dx[i],ny=exp.y+dy[i];
            if(nx<1||ny<1||nx>20||ny>20||vis[nx][ny]) continue;
            vis[nx][ny]=1;
            tail++;
            q[tail].x=nx;
            q[tail].y=ny;
            q[tail].t=exp.t+1;
            if(nx==1&&ny==1) return q[tail].t;
        }
    }
}
