using namespace std;
long long f[65][65];//一定要开long long
int main(){
	int n;scanf("%d",&n);
	for(int i=0;i<=n;i++) f[i][0]=1;
	for(int j=1;j<=n;j++) f[0][j]=0;
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++){
