      end;//从C盘里“偷来”的快排
function fei(k:longint):longint;
var x,y,p:longint;
begin
x:=1;y:=1;
while y<=k do
  begin
  p:=x;x:=y;y:=y+p;
  end;
exit(x);
end;//刚才的fei
