#include<bits/stdc++.h>
using namespace std;
const int maxn=1010;
struct node
{
    int w,v;
}group[maxn][maxn];
int num[maxn],f[maxn];
int m,n,maxl=INT_MIN;
int main()
{
    scanf("%d%d",&m,&n);
    for(int i=1;i<=n;i++)
        {
            int tw,tv,tg;
            scanf("%d%d%d",&tw,&tv,&tg);
            group[tg][++num[tg]].w=tw;
            group[tg][num[tg]].v=tv;
            maxl=max(maxl,tg);
        }
    for(int i=1;i<=maxl;i++)
        if(num[i])
            for(int j=m;j>=0;j--)
                for(int k=1;k<=num[i];k++)
                    if(j>=group[i][k].w)
                        f[j]=max(f[j],f[j-group[i][k].w]+group[i][k].v);
    printf("%d\n",f[m]);
    return 0;
}
```

