```cpp
#include<iostream>
#include<cstdio>
typedef long long ll;
struct one
{
	ll v,w;
}a[101][1001];
ll f[1001][1001],len[101];
ll n,m;
ll max(ll a,ll b)
{
	return a>b?a:b;
}
ll dfs(ll x,ll m)
{
	if(f[x][m])return f[x][m];
	if(x>100)return 0;
	ll res=dfs(x+1,m);
	for(ll i=1;i<=len[x];++i)
	{
		if(m<a[x][i].v)continue;
		res=max(res,dfs(x+1,m-a[x][i].v)+a[x][i].w);
	}
	return f[x][m]=res;
}
int main()
{
	scanf("%lld%lld",&m,&n);
	ll v,w,k;
	for(ll i=1;i<=n;++i)
	{
		scanf("%lld%lld%lld",&v,&w,&k);
		a[k][++len[k]]=(one){v,w};
	}
