        for (int j=0;j<=m;j++)
            for (int k=0;k<=n;k++)
            {
                t=f[cur][j][k];
                if (i+j-k>m||i+j-k<0) continue;//越界退出
                if (a[i+1]==a[k+1]) add(t,f[cur^1][j][k+1]);//状态转移
                if (b[j+1]==b[i+j-k+1]) add(t,f[cur][j+1][k]);
                if (a[i+1]==b[i+j-k+1]) add(t,f[cur^1][j][k]);
