```cpp
#include <cstdio>
#include <cstring>
#include <cmath>

#define LL long long
#define MAXN 1010
#define max(x,y) (x > y ? x : y)

LL a, b, depth;
bool flag;
LL temp[MAXN], ans[MAXN];	// 分别记录当前答案与最终答案。

LL gcd (LL x, LL y) {
    return (!y ? x : gcd (y, x % y));
}	// 求最大公约数。

void dfs (LL newa, LL newb, LL step) {
    if (step + 1 == depth) {
        if (newa == 1) {
            temp[step + 1] = newb;
            flag = true; 
            if (temp[depth] < ans[depth] || !ans[depth]) {
                memcpy (ans, temp, sizeof (temp));
            }
        }

        return ;
    }

    LL first = newb % newa ? newb / newa + 1 : newb / newa;
    for (LL i = max (temp[step] + 1, first); i <= ceil (newb / newa) * (depth - step); i++) {
        temp[step + 1] = i;
        LL nxta, nxtb, g;
        nxta = newa * i - newb;
        nxtb = newb * i;
        g = gcd (nxta, nxtb);
        dfs (nxta / g, nxtb / g, step + 1);
    }
}	// 搜索主体。

int main (void) {

    scanf ("%d %d", &a, &b);
    LL g = gcd (a, b);
    temp[0] = 1;

    for (depth = 1; ; depth++) {
        dfs (a / g, b / g, 0);
        if (flag) { break ; }
    }
    for (int i = 1; i <= depth; i++) {
        printf ("%d ", ans[i]);
    }

    return 0;
}
