#include<bits/stdc++.h>
#define MAX 205
#define MIN 105
using namespace std;
int n,v[MIN][MAX],l[MIN],sum;//全局变量不用初始化清零
inline void dfs(int x,int y)//inline加速调用
{
	v[x][y]=0;//记录已经走过
	if(x+1<=n&&v[x+1][y])//判断是否有人以及超出边界
	dfs(x+1,y);//继续搜
	if(x-1>0&&v[x-1][y])
	dfs(x-1,y);
	if(y+1<=l[x]&&v[x][y+1])
	dfs(x,y+1);
	if(y-1>0&&v[x][y-1])
	dfs(x,y-1);
}
int main()
{
    string s;
    scanf("%d",&n);
    getline(cin,s);//这样能读掉n后面的回车
    for(int i(1);i<=n;++i)
    {
    	getline(cin,s);//读入包括空格的一整行
    	l[i]=s.size();//记录边界
    	for(int j(1);j<=l[i];++j)
    	if('a'<=s[j-1]&&s[j-1]<='z')
    	v[i][j]=1;//记录此处是否有人
	}
	for(int i(1);i<=n;++i)
	for(int j(1);j<=l[i];++j)
	if(v[i][j])
	{
		dfs(i,j);
		++sum;//总数++
	}
	printf("%d",sum);
}
```
