	for(int i=1; i<=(1<<N); i++)
		Dp[0][i]=1;
	for(int i=1; i<=N; i++) {
		int len=(1<<(i-1));
		for(int j=1; j<=(1<<N); j++) {
			int bl=(j-1)/len+1,l,r;
			if(bl&1) {
				l=bl*len+1;
				r=(bl+1)*len;
			}
			else{
