			}
			for(int k=l; k<=r; k++)
				Dp[i][j]+=Dp[i-1][j]*Dp[i-1][k]*P[j][k];
			if(i==N&&Dp[i][j]>Dp[i][res]+eps)
				res=j;
		}
	}
	cout<<res<<endl;
	return 0;
}
