这道题是一道非常好的spfa的题
题目中的变化点就是再给定的时间区间内会对有些码头进行封锁
这就需要对于特定的时间区域内进行枚举
所以我们ww[i][j]跑spfa就是再i到j天的时间内跑出的最短路
dp就很简单且容易思考了@Tyher
```
#include<bits/stdc++.h>
#define N 21
#define M 200
#define LL long long
using namespace std;
inline int gi(){
    char ch=getchar();int x=0,q=0;
    while(ch<'0' || ch>'9') ch=='-'?q=1:0,ch=getchar();
    while(ch>='0' && ch<='9') x=x*10+ch-'0',ch=getchar();
    return q?(-x):x;
}
int n,m,k,e,d;
struct Edge{
	int next,to,w;
}h[M<<1];
int block[N][105],ww[105][105];
LL dp[105];
int tot,head[N];
int dis[N],exist[N];
bool vis[N];
void Add(int u,int v,int w){
	h[++tot].next=head[u];
	h[tot].to=v;
	h[tot].w=w;
	head[u]=tot;
}
queue<int >q;
int spfa(int a,int b){
	memset(dis,63,sizeof(dis));
	memset(vis,0,sizeof(vis));
	for(int i=1;i<=m;i++) exist[i]=1;
	for(int i=1;i<=m;i++)
		for(int j=a;j<=b;j++)
			if(block[i][j])
				exist[i]=0;
    q.push(1);vis[1]=1;dis[1]=0;
    while(!q.empty()){
        int u=q.front();q.pop();vis[u]=0;
        for(int i=head[u];i;i=h[i].next){
            int v=h[i].to;
            if(!exist[v]) continue;
            if(dis[u]+h[i].w<dis[v]){
                dis[v]=dis[u]+h[i].w;
                if(!vis[v]) q.push(v),vis[v]=1;
            }
        }
    }
    return dis[m];
}
int main(){
	//freopen("trans.in","r",stdin);
	//freopen("trans.out","w",stdout);
    n=gi();m=gi();k=gi();e=gi();
	for(int i=1,u,v,w;i<=e;i++){
		u=gi();v=gi();w=gi();
		Add(u,v,w);Add(v,u,w);
	}
	d=gi();
	for(int i=1,p,a,b;i<=d;i++){
		p=gi();a=gi();b=gi();
		for(int i=a;i<=b;i++) block[p][i]=1;
	}
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++)
			ww[i][j]=spfa(i,j);
	for(int i=1;i<=n;i++){
		dp[i]=(LL)ww[1][i]*i;
		for(int j=1;j<i;j++)
			dp[i]=min(dp[i],dp[j]+k+(LL)ww[j+1][i]*(i-j));
	}
	cout<<dp[n]<<endl;
	return 0;
}

