	int i,to,cur,cost;
	queue<int>p;p.push(1);
	memset(d,INF,sizeof(d)),d[1]=0,vis[1]=1;
	while(!p.empty()) {
		cur=p.front(),p.pop(),vis[cur]=0;
		for(i=head[cur];i;i=b[i].nxt) {
			to=b[i].to;
			if(usd[to]) continue;
			cost=d[cur]+b[i].cost;
			if(d[to]>cost) {
