		for(j=i+1;j<=T;j++) {
			for(k=1;k<=n;k++) usd[k]|=gg[k][j];
			Spfa();if(d[n]!=INF) add(i,j,d[n]*(j-i+1));//如果不连通，就不能连，否则就会嘿嘿嘿
		}
	}Dijstra();
	printf("%d",s[n][T]);
	return 0;
}
