```cpp
#include<bits/stdc++.h>
#define LL long long
#define maxn 25
#define maxe 1000
#define inf 0x3f3f3f3f
using namespace std;

struct Node{
	int t, next;
	LL c;
	Node(int _t, LL _c, int _next){
		t = _t;
		c = _c;
		next = _next;
	}
	Node(){

	} 
	bool operator < (Node n) const{
		return c > n.c;
	}
};

vector<int> nt[maxn];

Node ed[maxe];

int head[maxn], tot;

LL dis[maxn], dp[110];

bool vis[maxn];

void addedge(int f, int t, LL c){
	ed[tot] = Node(t, c, head[f]);
	head[f] = tot++;
}

priority_queue<Node> p;

void dijkstra(int s, int t){
	p.push(Node(s, 0, 0));
	for(int i = 0; i < maxn; ++i){
		dis[i] = inf;
	}
	while(!p.empty()){
		Node now = p.top();
		p.pop();
		if(vis[now.t]){
			continue;
		}
		vis[now.t] = true;
		dis[now.t] = now.c;
		for(int i = head[now.t]; i != -1; i = ed[i].next){
			p.push(Node(ed[i].t, now.c + ed[i].c, 0));
		}
	}
}

int main(){
	int n, m, e;
	LL K;
	scanf("%d %d %lld %d", &n ,&m, &K, &e);
	int f, t;
	LL c;
	memset(head, -1, sizeof head);
	tot = 0;
	for(int i = 0; i < e; ++i){
		scanf("%d %d %lld", &f, &t, &c);
		addedge(f, t, c);
		addedge(t, f, c);
	}
	int ti, l, r, now;
	scanf("%d", &ti);
	for(int i = 0; i < ti; ++i){
		scanf("%d %d %d", &now, &l, &r);
		for(int j = l; j <= r; ++j){
			nt[j].push_back(now);
		}
	}
	dp[0] = 0;
	for(int i = 1; i <= n; ++i){
		memset(vis, 0, sizeof vis);
		for(int j = 1; j <= i; ++j){
			for(int k = 0; k < nt[j].size(); ++k){
				vis[nt[j][k]] = 1;
			}
		}
		dijkstra(1, m);
		dp[i] = dis[m] * i;
		for(int j = 0; j < i; ++j){
			memset(vis, 0, sizeof vis);
			for(int k = j + 1; k <= i; ++k){
				for(int x = 0; x < nt[k].size(); ++x){
					vis[nt[k][x]] = 1;
				}
			}
			dijkstra(1, m);
			dp[i] = min(dp[i], dp[j] + (i - j) * dis[m] + K);
		}
	}
	printf("%lld\n", dp[n]);
	return 0;
}
