const int MAX=25;
const long long INF=200005;
int n,m,k,e,d;
long long sea[MAX][MAX],st[105],ed[105],po[105],ans[105][105],f[105];
bool use[MAX];
long long dijkstra()//数据小，妈妈再也不会担心MLE啦
{
	int start,end;
	long long dist[MAX];
	bool went[MAX];
	for(int i=1;i<=m;i++)
	{
		dist[i]=sea[1][i];
		went[i]=false;
	}
