	went[1]=true;
	for(int i=2;i<=m;i++)
	{
		int tmp=INF;
		for(int j=1;j<=m;j++)
		{
			if(sea[i][j]!=INF&&!went[j]&&tmp>dist[j]&&use[j])
			{
				tmp=dist[j];
				start=j;
			}
		}
		went[start]=true;
		for(end=1;end<=m;end++)
			if(sea[start][end]!=INF&&start!=end)
