#include<bits/stdc++.h>
using namespace std;
#define ll long long
#define FCC fclose(stdin),fclose(stdout)
#define pr pair<int,ll> 
#define MP make_pair
const int INF = 0x3f3f3f3f,N = 22,M = N*N,D = 105;
inline ll read()
{
	ll ret=0;char ch=' ',c=getchar();
	while(!(c>='0'&&c<='9')) ch=c,c=getchar();
	while(c>='0'&&c<='9') ret=(ret<<1)+(ret<<3)+c-'0',c=getchar();
	return ch=='-'?-ret:ret;
}
int head[N],ecnt=-1;
int d,n,m,k,pass[N][D];
void init_edge(){memset(head,-1,sizeof(head)),ecnt=-1;}
struct edge
{
	int nxt,to,w;
}a[M<<1];
inline void add_edge(int x,int y,int w)
{
	a[++ecnt]=(edge){head[x],y,w};
	head[x]=ecnt;
}
int dis[N];
ll dp[D],val[D][D];
bool vis[N],mp[N];
void dijk()
{
	priority_queue<pr,vector<pr>,greater<pr> >q;
	memset(dis,0x3f,sizeof(dis));
	memset(vis,0,sizeof(vis));
	dis[1]=0ll,q.push(MP(0,1));
	while(!q.empty())
	{
		int u=q.top().second;
		q.pop();
		if(vis[u]) continue;
		vis[u]=1; 
		for(int i=head[u];~i;i=a[i].nxt)
		{
			int v=a[i].to;
		//	printf("%d->%d\n",u,v);
			if(vis[v]||mp[v]) continue;
			if(dis[v]>dis[u]+a[i].w)
			{
				dis[v]=dis[u]+a[i].w;
				q.push(MP(dis[v],v));
			}
		}
	}
}
int main()
{
	init_edge();
	d=read(),n=read(),k=read(),m=read();
	for(int i=1;i<=m;i++)
	{
		int u=read(),v=read(),w=read();
		add_edge(u,v,w),add_edge(v,u,w); 
	}
	int q=read();
	for(int i=1;i<=q;i++)	
	{
		int x=read(),l=read(),r=read();
		for(int j=l;j<=r;j++) pass[x][j]=1;	
	}
	for(int i=1;i<=d;i++)	
		for(int j=i;j<=d;j++)
		{
			memset(mp,0,sizeof(mp));
			for(int u=1;u<=n;u++)
				for(int r=i;r<=j;r++)	
					mp[u]|=pass[u][r];
			dijk();
			val[i][j]=dis[n];
	//		printf("val[%d][%d]=%lld\n",i,j,val[i][j]);
		}
	//memset(dp,0x3f,sizeof(dp)),dp[0]=0ll;
	for(int i=1;i<=d;i++) dp[i]=val[1][i]*i;

	for(int i=2;i<=d;i++)	
		for(int j=1;j<i;j++)
			dp[i]=min(dp[i],dp[j]+val[j+1][i]*(i-j)+k);
	printf("%lld\n",dp[d]);
	return 0;
}
