inline void add(int x,int y,int z)
{
	++tot;
	e[tot].u = x;
	e[tot].v = y;
	e[tot].w = z;
	e[tot].next = head[x];
	head[x] = tot;
}
int dis[233],cost[233][233];
bool vis[233];
inline bool check(int t1,int t2,int x)//检查该时间段 x 点是否无法卸载货物 
{
	for (rg int i = t1;i <= t2;++i)
	{
		if (ban[x][i]) return false;
	}
