		nums[i][i]=(s[i]-'0')%m;
	for(int i=1;i<=n;i++)
		for(int j=i+1;j<=n;j++)	{
			nums[i][j]=nums[i][j-1]*10+(s[j]-'0');
			nums[i][j]%=m;
		}
	for(int i=2;i<=n;i++)
		dp[i][nums[1][i]]=0;
	for(int i=1;i<=n;i++)
		for(int j=0;j<i;j++)
			for(int k=0;k<m;k++)	{
				int to=k*nums[j+1][i]%m;
				dp[i][to]=min(dp[i][to],dp[j][k]+1);
			}
	for(int k=0;k<m;k++)
		if(dp[n][k]<1e9)	{
