using namespace std;
const int MAXN=1e5+8;
int n,w;
int v[MAXN],c[MAXN],m[MAXN];
int f[MAXN];
int main()
{
	cin>>n>>w;
	int cnt=0;
	for(int i=1;i<=n;i++)
	{
		int a,b,m;
		cin>>a>>b>>m;
		for(int j=1;j<=m;j*=2)
		{
			v[++cnt]=j*a,c[cnt]=j*b;
			m-=j;
		}
		if(m)
			v[++cnt]=m*a,c[cnt]=m*b;
	}
	for(int i=1;i<=cnt;i++)
		for(int j=w;j>=c[i];j--)
			f[j]=max(f[j],f[j-c[i]]+v[i]);
