void read(int &n)
{
    char c='+';int x=0;bool flag=0;
    while(c<'0'||c>'9'){c=getchar();if(c=='-')flag=1;}
    while(c>='0'&&c<='9')
    x=(x<<1)+(x<<3)+c-48,c=getchar();
    flag==1?n=-x:n=x;
}
int n,m;
struct node
{
    int va,we,num;
}a[MAXN];
int dp[MAXN];
