{
    read(n);read(m);
    for(int i=1;i<=n;i++)
    {
        read(a[i].va);
        read(a[i].we);
        read(a[i].num);
    }
    for(int i=1;i<=n;i++)// 枚举每一个物品 
    {
        int left=(a[i].num);
        for(int k=1;left;k<<=1)// 将每一个物品以二进制表示 
        {
            if(k>left)
                k=left;// 处理最后不能表示成二进制的情况 
            left-=k;// 每次都减去 
            int w=a[i].we*k;
            int v=a[i].va*k;// 常数优化 
            for(int j=m;j>=w;j--)
                dp[j]=max(dp[j],dp[j-w]+v);//最基本的多重背包DP 
        }
    }
    printf("%d",dp[m]);
    return 0;
}
