#include<cstdio>
#include<cstdlib>
#include<ctime>
#include<iostream>
#include<cstring>
using namespace std;
int n;
struct peo{//结构体 
    char ti[105];//票数 
    int le,nu;//票数的位数 号数 
}p[25];
void asort(int l,int r){//手写排序 
    if(l>=r) return;
    swap(p[l],p[rand()%(r-l+1)+l]);
    int i=l+1,lt=l,rt=r+1;
    while(i<rt){//位数大的在前 
        if(p[i].le==p[l].le) i++;
        else if(p[i].le>p[l].le) swap(p[i],p[lt+1]),lt++,i++;
        else swap(p[i],p[rt-1]),rt--;
    }
    swap(p[l],p[lt]);
    asort(l,lt-1);
    asort(rt,r);
}
int m(int x,int y){//比较票数大小 
    for(int j=1;j<=p[1].le;j++){
        if(p[x].ti[j]>p[y].ti[j]) return 1;
        if(p[x].ti[j]<p[y].ti[j]) return 0;
    }
    return 1;
}
int main(){
    scanf("%d ",&n);
    for(int i=1;i<=n;i++){
        gets(p[i].ti+1);
        p[i].le=strlen(p[i].ti+1);
        p[i].nu=i;
    }
    asort(1,n);
    int ma=1;//最大的号数 
    for(int i=2;p[i].le==p[1].le;i++)
        if(m(i,ma)) ma=i;//确定最终最大值 
    printf("%d\n",p[ma].nu);
    puts(p[ma].ti+1);
    return 0;
}
