struct bigNumber
{
    int len;
    long long num[100];
    inline bool operator >(const bigNumber& T)const
    {
        if(len<T.len)
            return false;
        if(len>T.len)
            return true;
        PER(ii,len,1)
        {
            if(num[ii]<T.num[ii])
                return false;
            if(num[ii]>T.num[ii])
                return true;
        }
        return false;
