 ```cpp
#include<cstring>
#include<string>
#include<cstdio>
#include<cmath>
#include<iostream>
#include<algorithm>
#include <cstdlib>
//BY cxj
using namespace std;
int xy;//用来判断 是否已找到结果 
int a[10][10],v[10][10],bx[10][10],by[10][10],bg[10][10];
//a:地图，
//bx：某行某数是否被调用过，
//by：某列某数是否被调用过，
//v:该点是否可改 ,      
//bg:某宫某数是否被调用过

int q(int x,int y,int s,int ss) 
 //改x,y宫s数是否被调用过，1：用过；0：没有 
{
	if (x<=3&&y<=3)
        bg[1][s]=ss;

    if (x<=3&&y>3&&y<=6)
        bg[2][s]=ss;

	if (x<=3&&y>6)
        bg[3][s]=ss;

    if (x>3&&x<=6&&y<=3)
        bg[4][s]=ss;

	if (x>3&&x<=6&&y>3&&y<=6)
        bg[5][s]=ss;

    if (x>3&&x<=6&&y>6)
        bg[6][s]=ss;

	if (x>6&&y<=3)
        bg[7][s]=ss;

    if (x>6&&y>3&&y<=6)
        bg[8][s]=ss;

	if (x>6&&y>6)
        bg[9][s]=ss;

	return 0;
}

int p(int x,int y,int s) 
//查x,y宫s数是否被调用过
{
	if (x<=3&&y<=3)
        return bg[1][s];

    if (x<=3&&y>3&&y<=6)
        return bg[2][s];

	if (x<=3&&y>6)
        return bg[3][s];

    if (x>3&&x<=6&&y<=3)
        return bg[4][s];

	if (x>3&&x<=6&&y>3&&y<=6)
        return  bg[5][s];

    if (x>3&&x<=6&&y>6)
        return bg[6][s];

	if (x>6&&y<=3)
        return bg[7][s];

    if (x>6&&y>3&&y<=6)
        return  bg[8][s];

	if (x>6&&y>6)
        return bg[9][s];

	return 0;
}

int dfs(int x,int y)//逐个排查 
{
    if (x==10||xy==1)//找到答案，结束过程 
       {
       	xy=1;
       	return 0;
       }
    //cout<<"A"<<endl;
    if (a[x][y]==0) 
        {
		for (int i=1;i<=9;i++)
            if (bx[x][i]==0 && by[y][i]==0 && (!p(x,y,i)))
            	{
            	   	a[x][y]=i;
            	   	bx[x][i]=1;
            	   	by[y][i]=1;
            	   	q(x,y,i,1);

            	   	if (y==9)
            	   	   	dfs(x+1,1);
            	   	else
            	   	   	dfs(x,y+1);

            	   	if (xy==1)
       				   return 0;

            	   	bx[x][i]=0;//回溯 
            	   	a[x][y]=0;
            	   	by[y][i]=0;
            	   	q(x,y,i,0);
            	}
            } 
    else
    {
        if (y==9)//换行 
            dfs(x+1,1);
        else
            dfs(x,y+1);

        if (xy==1)
       		return 0;
    }

    return 0;
}

int main()
{
    for (int i=1;i<=9;i++)
        for (int j=1;j<=9;j++)
            {
            	cin>>a[i][j];

            	if (a[i][j]!=0)//处理固定点 
            	   {
            	   	v[i][j]=2;
            	   	bx[i][a[i][j]]=1;
            	   	by[j][a[i][j]]=1;
            	   	q(i,j,a[i][j],1);
            	   }
            }

    xy=0; 
    dfs(1,1);

    //cout<<endl;
    for (int i=1;i<=9;i++)//输出 
        {
        	for (int j=1;j<=9;j++)
        	    cout<<a[i][j]<<" ";
        	cout<<endl;
        }

    return 0;
}

