			cout<<endl;
		}
		return true;
	}
	if(suduko[cur]!=0)
		return dfs(cur+1);
	for(int i=1;i<=9;i++)
	{
		if(!line[(cur-1)/9+1][i]&&!roll[cur%9][i]&&!gong[sg[cur]][i])
		{
			line[(cur-1)/9+1][i]=true;
			roll[cur%9][i]=true;
			gong[sg[cur]][i]=true;
			suduko[cur]=i;
			if(dfs(cur+1))
				return true;
			line[(cur-1)/9+1][i]=false;
			roll[cur%9][i]=false;
			gong[sg[cur]][i]=false;
			suduko[cur]=0;
		}
	}
	return false;
}
