	{

		if (t1[x1[b+1]][i]==0&&t2[y1[b+1]][i]==0&&t3[(x1[b+1]-1)/3+1][(y1[b+1]-1)/3+1][i]==0)
		{
		t1[x1[b+1]][i]=1;//行
		t2[y1[b+1]][i]=1;//列
		t3[(x1[b+1]-1)/3+1][(y1[b+1]-1)/3+1][i]=1;//边界
		a[x1[b+1]][y1[b+1]]=i;
		dfs(b+1);
		a[x1[b+1]][y1[b+1]]=0;
		t1[x1[b+1]][i]=0;
		t2[y1[b+1]][i]=0;
		t3[(x1[b+1]-1)/3+1][(y1[b+1]-1)/3+1][i]=0;	
		if (p)return;
		}
	}
}
int main()
{
	for (i=1;i<=9;++i)
	for (j=1;j<=9;++j)
	{
		cin>>a[i][j];
		if (a[i][j]==0)
		{
        //如果是空的，那么记录行列坐标
			++k;
			x1[k]=i;
			y1[k]=j;

		}
		else
		{
        //如果有数
			t1[i][a[i][j]]=1;
         //标记行
			t2[j][a[i][j]]=1;
            //标记列
			t3[(i-1)/3+1][(j-1)/3+1][a[i][j]]=1;
            //标记粗线格
		}
	}
	x1[k+1]=100;
	y1[k+1]=100;
	//边界
	dfs(0);
   //从0开始很重要，否则第一个空格会跳过
}
```

