# 方法二

```cpp
#include<cstdio>
#include<cmath>
#include<algorithm>
#include<iostream>
using namespace std;
int a[11][11];
bool sl(int x,int z)
{
	for(int i=1;i<=9;i++)
		if(a[x][i]==z)//固定x，枚举y 
			return 0;
	return 1;
}//搜索竖列 
bool hl(int y,int z)
{
	for(int i=1;i<=9;i++)
		if(a[i][y]==z)//固定y，枚举x 
			return 0;
	return 1;
}//搜索横列 
bool k(int x,int y,int z)
{
	for(int i=x*3-2;i<=x*3;i++)
	for(int j=y*3-2;j<=y*3;j++)//此处算式可以自己尝试，刚好搜索一个九宫格，不懂可参见上方图表。 
		if(a[i][j]==z)//九宫格中枚举x，y 
			return 0;
	return 1;
}//搜索块 
void dfs(int x,int y)
{
	if(x==10)
	{
		for(int i=1;i<=9;i++)
		{
			for(int j=1;j<=9;j++)
				printf("%d ",a[i][j]);
			printf("\n");
		}
		exit(0);
	}//满足条件时输出 
	int nx=x,ny=y+1;
	if(ny==10)//计算下一个要搜索的点。
	{
		nx=x+1;
		ny=1;
	}//行动列不动，ny越界时nx加一。
	if(a[x][y]==0)
		for(int i=1;i<=9;i++)
		{
			if(sl(x,i) && hl(y,i) && k((int)ceil((double)x/3),(int)ceil((double)y/3),i))
			{
			 a[x][y]=i;
			 dfs(nx,ny);
		 	 a[x][y]=0;//撤销标记 
			}
		}//如果满足条件，则进行填数。注意：（double）必须加，因为y是int型，所以ceil不会起作用，如果不理解算式可以参见上面的图表。
	else dfs(nx,ny);
}
int main()
{
	for(int x=1;x<=9;x++)
	for(int y=1;y<=9;y++)
		scanf("%d",&a[x][y]);//读入	
	dfs(1,1);//从（1,1）开始 
}
