```cpp
#include <bits/stdc++.h>//万能头
using namespace std;
int x,y,z;//输入数据
bool f=true;//判断能不能一下打死,先默认为可以
int main()//主函数
{
    scanf("%d%d%d",&x,&y,&z);//输入
    if(y<=0||y>315||y>=x||x>1800||z>=1000||z<=100)//首先判断错误数据
    {
        printf("Wrong Data");//注意不要出现字符串输出问题(即打错字)
        return 0;//防止重复直接结束
    }
    if(z*2<x)//再判断不能绝杀的情况
    {
        printf("No");//Boss:您已阵亡
        return 0;//依然直接结束
    }
    printf("Yes\n");//到这里就是可以绝杀了
    if(z<x)//看看能不能一次打死
    {
        f=false;//不能就把f设为false
    }
    if(f)//如果一次打死
    {
        int blood=z*0.33;//这里定义一个单独的变量来储存嗜血量,防止不必要的错误,先存储3星嗜血量
        bool k=false;//记录触发嗜血能否完胜
        if(blood+y>=315)//3星嗜血
        {
            printf("Great");
            k=true;//可以完胜
            return 0;//直接结束
        }
        blood=z*0.5;//更新为4星嗜血量
        if(blood+y>=315)//4星嗜血
        {
            printf("Eh..\nOK");//记得换行
            k=true;//可以完胜
            return 0;//直接结束
        }
        blood=z;//5星嗜血量
        if(blood+y>=315)//5星嗜血
        {
            printf("Oh..\nOh..\nYes , If he can get 5-star.");//此处很容易打错字!!!注意!!!
            k=true;//可以完胜
            return 0;//直接结束
        }
        if(!k)//如果都不行
        {
            printf("No\nNo\nNo\n");//输出3行No
            blood=z*0.5;//4星嗜血量
            int ans=y+blood;//存储答案
            printf("%d",ans);//输出答案
            return 0;//直接结束
        }
    }
    else//后面两次打死跟前面差不多是一样的,只是嗜血量要乘2,所以不多做注释
    {
        int blood=z*0.33;
        bool k=false;
        if(2*blood+y>=315)
        {
            printf("Great");
            k=true;
            return 0;
        }
        blood=z*0.5;
        if(2*blood+y>=315)
        {
            printf("Eh..\nOK");
            k=true;
            return 0;
        }
        blood=z;
        if(2*blood+y>=315)
        {
            printf("Oh..\nOh..\nYes , If he can get 5-star.");
            k=true;
            return 0;
        }
        if(!k)
        {
            printf("No\nNo\nNo\n");
            blood=z*0.5;
            int ans=y+2*blood;//依然要乘2
            printf("%d",ans);
            return 0;
        }
    }
    return 0;//其实这个加不加都无所谓,因为前面早就结束了,走不到这里
}
```

