	string name;
	int bg,level,zw,id;
}a[N];

bool cmp(node A,node B){
	return A.bg==B.bg?A.id<B.id:A.bg>B.bg;
}
bool cmp_(node A,node B){
	return A.zw==B.zw?A.level==B.level?A.id<B.id:A.level>B.level:A.zw<B.zw;
}
int main(){
	n=read();
	for (int i=0;i<7;i++)
	  mp[zw_name[i]]=i;
	for (int i=1;i<=n;i++){
		a[i].id=i;
		cin>>a[i].name>>s;
		a[i].zw=mp[s];
		a[i].bg=read();a[i].level=read();
	}
	sort(a+4,a+n+1,cmp);
    //赋予新职位
	for (int i=4;i<=n;i++)
	  for (int j=2;j<7;j++)
	    if (i<=num[j]){
	    	a[i].zw=j;
	    	break;
	    }
	sort(a+1,a+n+1,cmp_);
	for (int i=1;i<=n;i++)
	  cout<<a[i].name<<' '<<zw_name[a[i].zw]<<' '<<a[i].level<<endl;
	return 0;
}
