```cpp
#include<bits/stdc++.h>
using namespace std;
int a[1000][1000]; 
int main(){
    int n,m,k,x,y,ans=0;
    scanf("%d %d %d",&n,&m,&k);
    for(int i=1;i<=m;i++){//火把 
        scanf("%d %d",&x,&y);
        x+=2;//方法就是整体往后移2格 
        y+=2;//y也一样 
        for(int j=x-2;j<=x+2;j++){
            a[j][y]=1;//把照到的地方变为1 
        }
        for(int j=y-2;j<=y+2;j++){
            a[x][j]=1;
        }
        a[x+1][y+1]=1;
        a[x-1][y-1]=1;
        a[x+1][y-1]=1;
        a[x-1][y+1]=1;
    }
    for(int i=1;i<=k;i++){//萤石 
        scanf("%d %d",&x,&y);
        x+=2;
        y+=2;
        for(int j=x-2;j<=x+2;j++){
            for(int k=y-2;k<=y+2;k++){
                a[j][k]=1;
            }
        }
    }
    for(int i=3;i<=n+2;i++){//因为数组后移2位，要从3开始找 
        for(int j=3;j<=n+2;j++){
            if(a[i][j]==0){//没照到时答案+1 
                ans++;
            }
        }
    }
    printf("%d\n",ans);
    return 0;
}

