#include<bits/stdc++.h>
using namespace std;
int n,m,k,a,b,i,j,k1,s[10005][10005],sum,t,t1;
int main()
{
    cin>>n>>m>>k;
    for (i=1; i<=m; i++)
    {
        cin>>a>>b; //读入位置
        t=b;    //按打印图形的方法，从t到t1标1，火把第一行是从第b列开始的
        t1=b;
        for (j=a-2; j<=a; j++)
        {
            for (k1=t; k1<=t1; k1++) s[j][k1]=1; //打印上半部分的三角形，标为1
            t--;t1++;
        }
        for (j=a; j<=a+2; j++)
        {
            t++;t1--;
            for (k1=t; k1<=t1; k1++) s[j][k1]=1; //打印下半部分的倒三角形，标为1
        }
    }
    for (i=1; i<=k; i++)
    {
        cin>>a>>b;
        for (j=a-2; j<=a+2; j++)
         for (k=b-2; k<=b+2; k++)
         s[j][k]=1;  //照到的位置标1
    }
    for (i=1; i<=n; i++)
     for (j=1; j<=n; j++)
     if (s[i][j]==0) sum++;
    cout<<sum;
}
