**对了！，还要告诉你们一个坑，（一直错却不知道为啥的看过来）萤石的光照面积是5×5而不是5×4**


下面是代码


```cpp
#include<cstdio>
using namespace std;
const int N = 10001;
int n, m, k, ans;
int dx[] = {0, -1, -2, 0, 0, 1, 2, 0, 0, -1, 1, 1, -1}, dy[] = {0, 0, 0, 1, 2, 0, 0, -1, -2, -1, 1, -1, 1};
bool a[N][N];
inline int read()
{
    int res = 0;
    bool f = 0;
    char ch;
    while (((ch = getchar()) < '0' || ch > '9') && ch != '-');
    if (ch == '-') f = 1;
      else res = ch - '0';
    while ((ch = getchar()) >= '0' && ch <= '9')
      res = (res << 3) + (res << 1) + ch - '0';
    return f? ~res + 1 : res;
}
inline void w(int ee)
{
    if (ee > 9) w(ee / 10);
    putchar(ee % 10 + '0');
    return;
}
int main()
{
    n = read(); 
    m = read();
    k = read();
    for (int i = 1; i <= m; i++)
    {
          int x = read(), y = read();
          for (int j = 0; j <= 12; j++)
          if (x + dx[j] > 0 &&  x + dx[j] <= n && y + dy[j] > 0 && y + dy[j] <= n) 
              a[x + dx[j]][y + dy[j]] = 1;
    }
    for (int i = 1; i <= k; i++)
    {
        int x = read() - 2, y = read() - 2;
        for (int j = 0; j <= 4; j++)
          for (int k = 0; k <= 4; k++)
            if (x + j > 0 && x + j <= n && y + k > 0 && y + k <= n)
              a[x + j][y + k] = 1;
    }
    for (int i = 1; i <= n; i++)
      for (int j = 1; j <= n; j++)
        if (!a[i][j]) ans++;
    w(ans);
    return 0;
}
