        if ((y+2)<=m)    y2=y+2;
        else if ((y+1)<=m)    y2=y+1;
        for (x=x1;x<=x2;x++)
        {
            for (y=y1;y<=y2;y++)
            {
                f[x][y]=true;
            }
        }
    }
    guanggeshu=0;
    for (x=1;x<=m;x++)
    {
        for (y=1;y<=m;y++)
        {
            if (f[x][y])
            {
                 guanggeshu++;
            }
        }
    }
    cout<<m*m-guanggeshu;
    return 0;
}
```

