{
    int V, G, N;
    cin >> V >> G >> N;
    for (int i = 1; i <= N;i++)
    {
        int t, v, g;
        cin >> t >> v >> g;
        for (int j = V; j >= v;j--)
        {
            for (int k = G; k >= g;k--)
            {
                f[j][k] = max(f[j][k], f[j - v][k - g] + t);
            }
        }
    }
    printf("%d\n", f[V][G]);
    return 0;
}

