#include <algorithm>
#include <array>
#include <bitset>
#include <cstdio>
std::array<int, 1500010> a;
std::array<int, 1500010> sortedA;
std::bitset<1000000010> series;
int main(void)
{
    int max, n, zero;
    max = 0;
    zero = 0;
    a.fill(0);
    sortedA.fill(0);
    std::scanf("%d", &n);
    for (int i = 0; i < n; i++)
    {
        std::scanf("%d", &(a.at(i)));
        sortedA.at(i) = a.at(i);
    }
    std::stable_sort(sortedA.begin(), sortedA.begin() + n);
    max = sortedA.at(n - 1);
    for (int i = 0; i < max; i++)
    {
        series[i] = 1;
        i += zero;
        ++zero;
    }
    for (int i = 0; i < n; i++)
    {
        std::printf("%d\n", int(series[a.at(i) - 1]));
    }
    return 0;
}
```
