#include<stdio.h>//stdio.h万岁！
int min(int a,int b)
{
	return a<b?a:b;
}
int read()//快读
{
    int re=0;
    char c=getchar();
    while((c<'0'||c>'9')&&c!='-')c=getchar();
    if(c=='-')
    {
        c=getchar();
        while(c>='0'&&c<='9')
        {
            re=(re<<1)+(re<<3)+(c^48);
            c=getchar();
        }
        return -re;
    }
    else
    {
        while(c>='0'&&c<='9')
        {
            re=(re<<1)+(re<<3)+(c^48);
            c=getchar();
        }
        return re;
    }
}
struct data
{
    int a,b;
}d[1000001];
int n,lst,cnt;
int pile[2000001];//除了sort()只会堆排so..
void push(int x)//将元素x加入堆
{
    pile[++pile[0]]=x;
    int i=pile[0];
    while(d[pile[i]].b<d[pile[i>>1]].b&&i>1)
    {
        pile[i]^=(pile[i>>1]^=(pile[i]^=pile[i>>1]));
        i>>=1;
    }
}
void pop()//去掉堆顶元素
{
    pile[1]=pile[pile[0]--];
    int i=1,j=2;
    while(d[pile[i]].b>min(d[pile[j]].b,d[pile[j+1]].b)&&j<=pile[0])
    {
    	if(d[pile[j]].b>d[pile[j+1]].b)
    	{
    		pile[i]^=pile[j+1]^=pile[i]^=pile[j+1];
    		i=j+1;
		}
		else
		{
			pile[i]^=pile[j]^=pile[i]^=pile[j];
			i=j;
		}
        j=i<<1;
    }
}
int main()
{
    n=read();
    for(int i=1;i<=n;++i)
    {
        d[i].a=read();
        d[i].b=read();
        push(i);
    }
    while(pile[0])
    {
        if(d[pile[1]].a>=lst)
        {
            ++cnt;
            lst=d[pile[1]].b;
        }//只有可行才选择，否则直接pop()
        pop();
    }
    printf("%d\n",cnt);
    return 0;
}
