	sort(a+1,a+n+1,cmp);//按照开始的时间点排序
	a[0].bg=-1;
	for(int i=1;i<=n;i++)
		if(a[i].bg!=a[i-1].bg) b[++cnt]=a[i];
    //可以保证开始时间一定要找最先结束的
	for(int i=1;i<=cnt;i++)
	{
		for(int j=i-1;j>=max(1,i-1224);j--)
        //向前找随机个，答案必然很接近
		{
			if(b[j].ed<=b[i].bg)
				dp[i]=max(dp[i],dp[j]+1);
		}
	}
	printf("%d\n",dp[cnt]+1);//最后加1
	return 0;
}
