    memset(f,0,sizeof(f));

    for (int i=1;i<=maxin;i++)
    {
        if (p[i]!=-1) f[i]=max(f[p[i]]+1,f[i-1]);
            else f[i]=f[i-1];
        ans=max(ans,f[i]);
    }

    printf("%d",ans);
}

