	//就假设其中一种情况，然后举出特殊栗子，再用这个栗子用第二张数据排序看一下
	//最后比较一下，就可以了，非常没有科学依据的方法，但是一般都是正确的
	//方法很辣鸡，但是很实用 
}
int sum=1;
int j=1;
int main() {
	scanf("%d",&n);
	for(int i=1; i<=n; i++)
		scanf("%d%d",&a[i].t1,&a[i].t2);//输入嘛 
	sort(a+1,a+1+n,cmp);//排序 
	int flag=a[1].t2;//flag用于比较a[i].t1是不是>=a[i-1].t2; 
	while(j<=n) {
		j++;//开始 
		if(a[j].t1>=flag) {//满足条件就刷新，并且计算个数sun++； 
			sum++;
			flag=a[j].t2;
		}
	}
	printf("%d",sum);//输出 
	return 0;
}
