	for(i=1;i<=n;i++) cin>>a[i].begin>>a[i].end;
	sort(a+1,a+n+1,cmp);
	for(i=1;i<=n;i++) if(a[i].begin>=m){m=a[i].end; ans++;}
         //如果这条线段与最新选择的线段不重合，更新答案
	cout<<ans<<endl;
	return 0;
}
