#include <iostream>

using namespace std;

int f[1000][10000],a[10000],g[10000][10000];

void a21(int a[],int b[])
{
	for(int i=1;i<=a[0];i++)
	{
		b[i]=a[i]*2;
	}
	b[1]++;
	for(int i=1;i<=a[0];i++)
	{
		b[i+1]+=b[i]/10;
		b[i]%=10;
	}
	if(b[a[0]+1]>0)
		b[0]=a[0]+1;
	else
		b[0]=a[0];
}

void gmf0(int g[],int f0[],int f[])
{
	for(int i=1;i<=g[0];i++)
	{
		f[i]=g[i]-f0[i];
	}

	for(int i=1;i<=g[0];i++)
	{
		if(f[i]<0)
		{
			f[i]+=10;
			f[i+1]--;
		}
	}
	int l=0;
	for(int i=g[0];i>=1;i--)
	{
		if(f[i]>0)
		{
				l=i;
				break;
		}

	}
	f[0]=l;
}

void fu(int f0[],int f[])
{
	for(int i=0;i<=f0[0];i++)
		f[i]=f0[i];
}

int main()
{
	int n;

	cin>>n;

	for(int i=1;i<=n;i++)
		cin>>a[i];

	g[1][0]=g[1][1]=1;

	for(int i=2;i<=n;i++)
		a21(g[i-1],g[i]);

	if(a[1] != 0)
		f[1][0]=f[1][1]=1;

	for(int i=2;i<=n;i++)
	{
		if(a[i]==0)
			fu(f[i-1],f[i]);
		else
			gmf0(g[i],f[i-1],f[i]);
	}

	for(int i=f[n][0];i>=1;i--)
		ocut<<f[n][i];

	return 0;
}

```

转移方程很简单。

我们设四个值:

$e[i]$，分别表示在$[1,i-1]$区间全部为关灯时，改变$i$处状态（关到开，开到关）。

$dpe[i]$，把$[1,i]$全部关灯的最小步数。

$dpl[i]$，把$[1,i-1]$全部关灯，$[i,i]$开灯的最小步数。

$a[i]$表示$i$的初始状态。

