#include <bits/stdc++.h>
using namespace std;

int n;
int a[1005];

struct num
{
    int len=1;
    int x[1005]={0,};
};

num operator + (const num &aa ,const num &bb)
{
    num an;
    int maxn=max(aa.len,bb.len);
    an.len=maxn;
    int c=0;
    for(int i=1;i<=maxn;i++)
    {
        an.x[i] = aa.x[i]+bb.x[i]+c;
        if(an.x[i]>=10){
            c=1;
            an.x[i]-=10;
        }
        else c=0;
    }
    if(c)
        an.len++, an.x[an.len]=1;
    return an;
}
bool operator < (const num &aa ,const num &bb)
{
    if(aa.len!=bb.len)return aa.len<bb.len;
    for(int i=aa.len;i>=1;i--)
        if(aa.x[i]!=bb.x[i])return aa.x[i]<bb.x[i];
}
bool operator > (const num &aa ,const num &bb)
{
    if(aa.len!=bb.len)return aa.len>bb.len;
    for(int i=aa.len;i>=1;i--)
        if(aa.x[i]!=bb.x[i])return aa.x[i]>bb.x[i];
}

void print(num an)
{
    for(int i=an.len;i>=1;i--)
        printf("%d",an.x[i]);
}

num e[1005];
num l[1005];
num dpl[1005];
num dpe[1005];
num one;
int main()
{
    scanf("%d",&n);
    for(int i=1;i<=n;i++)
