    e[1].x[1]=l[1].x[1]=1;
    e[1].len=l[1].len=1;
    dpe[1].len=dpl[1].len=1;
    one.x[1]=1;
    one.len=1;
    if(a[1])dpe[1].x[1]=1;
    else dpl[1].x[1]=1;
    for(int i=2;i<=n;i++)
